#[cfg(test)]
mod tests {
    use crate::interpreter::memory::Memory;

    #[test]
    fn test_memory() {
        let mut mem = Memory::new();
        mem.initialize();
        mem.load_rom("test_opcode.ch8");
        const EXPECTED_MEMORY: &str = "";
        assert_eq!(mem.dump_memory(), EXPECTED_MEMORY);
    }
}